import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';
import { Router } from '@angular/router';
import { TiresService } from 'src/app/services/api/tires.service';

@Component({
  selector: 'app-modal-tire-card',
  templateUrl: './modal-tire-card.component.html',
  styleUrls: ['./modal-tire-card.component.css']
})
export class ModalTireCardComponent implements OnInit {
  @Input() selectedTire: any;
  @Output() action = new EventEmitter();
  isClose: boolean = false;
  quantity: any;
  isOpen: boolean = false;
  selectedObject: any;

  constructor(private router: Router, private tiresService: TiresService) {
    this.quantity = 1;
    this.selectedTire = 1;
  }
  ngOnInit(): void {
    console.log(this.selectedTire);
    this.tiresService.getTireById(this.selectedTire).subscribe(data => {
    });
    this.isOpen = true;
  }

  ngAfterViewInit() {
    console.log(this.selectedTire)
  }

  incrementQuantity() {
    this.quantity++;
  }

  decrementQuantity() {
    if (this.quantity <= 1) {
      this.quantity = 1;
    } else {
      this.quantity--;
    }
  }

  redirectToShoppingCart() {
    this.router.navigateByUrl("/shoppingCart");
  }

}
