import { Component } from '@angular/core';

@Component({
  selector: 'app-sidebar',
  templateUrl: './sidebar.component.html',
  styleUrls: ['./sidebar.component.css']
})
export class SidebarComponent {
  filters = [
    {
      name: "Marca",
      values: [
        "DUNLOP",
        "FIRESTONE",
        "GOODYEAR",
        "HANKOOK",
        "INTERSTATE"
      ]
    },
    {
      name: "Precio",
      values: [
        "S/.100 - S/. 250",
        "S/.250 - S/. 500",
        "S/.500 - S/. 1000",
        "S/.1000 - S/. 2000",
      ]
    },
    {
      name: "Ancho",
      values: [155, 165, 175, 185, 195]
    },
    {
      name: "Aro",
      values: [12, 13, 14, 15, 16]
    }
  ]
}
