import { Component, Input, OnInit } from '@angular/core';
import { TiresService } from 'src/app/services/api/tires.service';

@Component({
  selector: 'app-tire-card',
  templateUrl: './tire-card.component.html',
  styleUrls: ['./tire-card.component.css']
})
export class TireCardComponent {
  @Input() tire: any;
  isSelected: boolean;
  selectedTire: any;
  constructor(private tiresService: TiresService) {
    this.tire = {};
    this.isSelected = false;
  }

  selectTire() {
    console.log(this.tire.id)
    // this.tiresService.getTireById(this.tire.id).subscribe(data => {
    //   this.selectedTire = data;
    //   console.log(this.selectedTire)
    // });
    this.selectTire = this.tire.id;
    setTimeout(() => {
      this.isSelected = true;
    }, 1000);
  }

  actions(events: boolean) {
    switch (events) {
      case false:
        this.isSelected = false;
        break;
    }
  }

}
