  selector: 'app-measurecard',
  templateUrl: './measurecard.component.html',
  styleUrls: ['./measurecard.component.css']
})
export class MeasurecardComponent {

  constructor(private router: Router) {

  }

  getCatalogue() {
    this.router.navigateByUrl("/catalogue");
